import React from "react";

import { Badge, Card, Col, Container, Row } from "react-bootstrap";

class movieDetails extends React.Component {
  state = {
    movie: null,
    movieIdFromTheSearchBar: this.props.match.params.id,
  };

  fetchMovies = async () => {
    try {
      const response = await fetch(
        "http://www.omdbapi.com/?apikey=ad2a416a&s=" +
          this.state.movieIdFromTheSearchBar
      );
      const result = await response.json();
      const movies = result.Search;
      const movie = movies.map((movie) => movie);
      //console.log(movie);

      //this.setState({ movie: fetching: false });
    } catch (e) {
      console.log(e);
      this.setState({ fetching: false });
    }
  };

  componentDidMount() {
    //console.log(this.props.match.params, "ggggggggg");
    this.fetchMovies();
  }

  render() {
    return (
      <Container>
        {this.state.movie && (
          <div>
            <Row className="my-2">
              <Col md={3}>
                <img
                  src={"/" + this.state.movie.Poster}
                  alt="movie"
                  className="img-fluid"
                />
              </Col>
              <Col md={9}>
                <Card>
                  <Card.Body>
                    <Card.Title>{this.state.movie.title}</Card.Title>
                    <Card.Subtitle>
                      <Badge variant="danger">{this.state.movie.title}</Badge>
                    </Card.Subtitle>
                    <Card.Text>{this.state.movie.description}</Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </div>
        )}
        {!this.state.movie && <h1>LOADING</h1>}
      </Container>
    );
  }
}

export default movieDetails;
