import React from "react";
import { Badge, Card, Col, Container, Row } from "react-bootstrap";

import Sliders from "./Carousel";

class movieDetails extends React.Component {
  state = {
    movie: null,
  };

  componentDidMount() {
    let movieIdFromTheSearchBar = this.props.match.params.stefano;
    let correctMovieToLoad = movies.find(
      (movie) => movie.id.toString() === movieIdFromTheSearchBar
    );
    this.setState({
      movie: correctMovieToLoad,
    });
  }

  render() {
    return (
      <Container>
        {this.state.movie && (
          <div>
            <Row className="my-2">
              <Col md={3}>
                <img
                  src={"/" + this.state.movie.Poster}
                  alt="movie"
                  className="img-fluid"
                />
              </Col>
              <Col md={9}>
                <Card>
                  <Card.Body>
                    <Card.Title>{this.state.movie.title}</Card.Title>
                    <Card.Subtitle>
                      <Badge variant="danger">{this.state.movie.label}</Badge>
                    </Card.Subtitle>
                    <Card.Text>{this.state.movie.description}</Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </div>
        )}
        {!this.state.movie && <h1>LOADING</h1>}
      </Container>
    );
  }
}

export default movieDetails;
