import React from "react";

import { Badge, Card, Col, Container, Row } from "react-bootstrap";

class movieDetails extends React.Component {
  state = {
    movie: null,
    movieTitle: this.props.match.params.Title,
    fetching: true,
  };

  fetchMovies = async () => {
    try {
      const response = await fetch(
        "http://www.omdbapi.com/?apikey=ad2a416a&s=" + this.state.movieTitle
      );

      const result = await response.json();
      this.setState({ movie: result, fetching: false });

      console.log(result);
    } catch (e) {
      console.log(e);
      this.setState({ fetching: false });
    }
  };

  componentDidMount() {
    //console.log(this.props.match.params, "ggggggggg");
    this.fetchMovies();
  }

  render() {
    return (
      <Container>
        {this.state.movie && (
          <div>
            <Row className="my-2">
              <Col md={3}>
                <img
                  src={"/" + this.state.Poster}
                  alt="movie"
                  className="img-fluid"
                />
              </Col>
              <Col md={9}>
                <Card>
                  <Card.Body>
                    <Card.Title>{this.result.Title}</Card.Title>
                    <Card.Subtitle>
                      <Badge variant="danger">{this.result.Title}</Badge>
                    </Card.Subtitle>
                    <Card.Text>{this.result.imdbID}</Card.Text>
                  </Card.Body>
                </Card>
              </Col>
            </Row>
          </div>
        )}
        {!this.result && <h1>LOADING</h1>}
      </Container>
    );
  }
}

export default movieDetails;
